// 1. REACH BACK to get the raw data from the Architect
// Make sure this name matches your node exactly!
const architectNodeName = 'Script Architect (Beat Sheet)';
const architectData = $(architectNodeName).first().json;

let outline = [];

// 2. INTELLIGENT PARSING (The Fix)
// We check multiple places where the AI might have hidden the data
let rawContent = architectData.output || architectData.content || architectData.message?.content || architectData;

// If it's a string (which your screenshot shows it is), we must clean and parse it
if (typeof rawContent === 'string') {
  // Remove any Markdown code blocks (```json ... ```) just in case
  const cleanContent = rawContent.replace(/```json/g, '').replace(/```/g, '').trim();

  try {
    const parsed = JSON.parse(cleanContent);
    // Now we look for the array inside the parsed object
    outline = parsed.script_outline || parsed.outline || parsed; 
  } catch (e) {
    // If parsing fails, output the error so we can see it
    return [{ json: { error: "JSON Parse Failed", raw: rawContent } }];
  }
} else if (typeof rawContent === 'object') {
  // If it was already an object (rare but possible)
  outline = rawContent.script_outline || rawContent;
}

// Safety check: Ensure we actually have an array
if (!Array.isArray(outline)) {
   return [{ json: { error: "Data found, but it is not an array", data: outline } }];
}

// 3. GET THE DOC ID from the immediate input
const googleDocId = $input.first().json.id;

// 4. MERGE THEM
return outline.map(scene => {
  return {
    json: {
      scene_number: scene.scene_number,
      scene_beat: scene.beat, 
      google_doc_id: googleDocId
    }
  };
});
